<?phpinclude 'defines.php';$post = (!empty($_POST)) ? true :false;if($post) {    $name = stripcslashes($_POST['name']);    $phone = stripcslashes($_POST['phone']);    $address = stripcslashes($_POST['address']);    $index = stripcslashes($_POST['index']);    $shipping = stripcslashes($_POST['shipping']);    $form = stripcslashes($_POST['type-form']);    switch ($form) {        case "getConsultation":            $typeForm = "Получить консультацию";            break;        case "getCall":            $typeForm = "Заказать звонок";            break;        case "getOrder1":            $typeForm = "Мобильный магазин";            break;        case "getOrder2":            $typeForm = "Терморюкзак";            break;        case "getOrder3":            $typeForm = "Терморазнос";            break;        case "getOrder4":            $typeForm = "Универсальный рюкзак";            break;    }    $subject = 'Заявка';    $error = '';    //utm    $mess = "";    if ($_GET["utm_source"] != '') {        $mess =	'<b>Источник: </b>'.$_GET["utm_source"].'<br>';    }    if ($_GET["utm_campaign"] != '') {        $mess .=	'<b>Кампания: </b>'.$_GET["utm_campaign"].'<br>';    }    if ($_GET["utm_term"] != '') {        $mess .=	'<b>Ключевое слово: </b>'.$_GET["utm_term"].'<br>';    }    if ($_GET["utm_medium"] != '') {        $mess .=	'<b>Тип трафика: </b>'.$_GET["utm_medium"].'<br>';    }    if(!empty($address) || !empty($index) || !empty($shipping)) {        $message = '            <html>                <head>                    <title>Заявка</title>                </head>                <body>                    <p>Ф.И.О. : '.$name.'</p>                    <p>Телефон: '.$phone.'</p>                    <p>Тип заказа: '.$typeForm.'</p>                    <p>Адрес доставки: '.$address.'</p>                    <p>Почтовый индекс: '.$index.'</p>                    <p>Способ оплаты: '.$shipping.'</p>                </body>            </html>';    } else {        $message = '            <html>                <head>                    <title>Заявка</title>                </head>                <body>                    <p>Имя: '.$name.'</p>                    <p>Телефон: '.$phone.'</p>                    <p>Назва Формы: '.$typeForm.'</p>                </body>            </html>';    }    if(!$error) {        $mail = mail(CONTACT_FORM, $subject, $message,            "From: TERMORUKZAKI.RU\r\n"            ."Content-type: text/html; charset=utf-8 \r\n"            ."X-Mailer: PHP/" . phpversion()        );        if($mail) {            echo "OK";        }    }    else {        echo '<div class="bg-danger">'.$error.'</div>';    }}